section.literallayout:
==== merge script ==== # Merge a branch into either the test or release branch
pname=$0
usage() { echo "Usage: $pname branch test|release" 1>&2 exit 1 }
git show-ref -q --verify -- refs/heads/"$1" || { echo "Can't see branch <$1>" 1>&2 usage }
case "$2" in test|release) if [ $(git log $2..$1 | wc -c) -eq 0 ] then echo $1 already merged into $2 1>&2 exit 1 fi git checkout $2 && git pull . $1 ;; *) usage ;; esac

git_config(git_default_config);
if (argc != 3)
        usage("git cat-file [-t|-s|-e|-p|<type>] <sha1>");
if (get_sha1(argv[2], sha1))
        die("Not a valid object name %s", argv[2]);
